import {promises as fs} from 'fs';
import ora from 'ora';
import path from 'path';
import {capitalize} from '../../helpers/string';
import {generateFile} from '../../helpers/template';

/**
 * Create a new controller file from the controller stub
 *
 * @param {string} name
 * @return {Promise<void>}
 */
export default async function makeController(name: string) {
  const spinner = ora().start();
  spinner.info(`Generating controller: ${name}`);
  const cwd = process.cwd();
  // @ts-ignore
  await fs.mkdir(`${cwd}/packages/functions/src/controllers`, {
    recursive: true
  });
  await generateFile(
    'controller',
    {
      name: capitalize(name)
    },
    path.resolve(
      cwd,
      `packages/functions/src/controllers/${name.toLowerCase()}Controller.js`
    )
  );
  spinner.succeed(`Congratulations! Generate controller: ${name} successfully`);
}
