import {promises as fs} from 'fs';
import ora from 'ora';
import path from 'path';
import {capitalize} from '../../helpers/string';
import {generateFile} from '../../helpers/template';

/**
 * Create a new middleware file from the middleware stub
 *
 * @param {string} name
 * @return {Promise<void>}
 */
export default async function makeMiddleware(name: string) {
  const spinner = ora().start();
  spinner.info(`Generating middleware: ${name}`);
  const cwd = process.cwd();
  await fs.mkdir(`${cwd}/packages/functions/src/middleware`, {recursive: true});
  await generateFile(
    'middleware',
    {
      name: capitalize(name)
    },
    path.resolve(
      cwd,
      `packages/functions/src/middleware/${name.toLowerCase()}.js`
    )
  );
  spinner.succeed(`Congratulations! Generate middleware: ${name} successfully`);
}
