import {promises as fs} from 'fs';
import ora from 'ora';
import path from 'path';
import {capitalize} from '../../helpers/string';
import {generateFile} from '../../helpers/template';

/**
 * Create a new page file for React with preset
 *
 * @param {string} name
 * @return {Promise<void>}
 */
export default async function makePage(name: string) {
  const spinner = ora().start();
  spinner.start(`Generating page: ${name}`);
  const cwd = process.cwd();

  await fs.mkdir(`${cwd}/packages/assets/src/pages/${capitalize(name)}`, {
    recursive: true
  });
  await generateFile(
    'page/page',
    {
      name: capitalize(name)
    },
    path.resolve(
      cwd,
      `packages/assets/src/pages/${capitalize(name)}/${capitalize(name)}.js`
    )
  );
  await generateFile(
    'page/index',
    {
      name: capitalize(name)
    },
    path.resolve(cwd, `packages/assets/src/pages/${capitalize(name)}/index.js`)
  );
  spinner.succeed(`Congratulations! Generate page: ${name} successfully`);
}
