import {promises as fs} from 'fs';
import ora from 'ora';
import path from 'path';
import {generateFile} from '../../helpers/template';

/**
 * Create a new repository file from the stub
 *
 * @param {string} name
 * @return {Promise<void>}
 */
export default async function makeRepository(name: string) {
  const spinner = ora().start();
  spinner.info(`Generating repository: ${name}`);
  const cwd = process.cwd();
  await fs.mkdir(`${cwd}/packages/functions/src/repositories`, {
    recursive: true
  });
  await generateFile(
    'repository',
    {
      name
    },
    path.resolve(cwd, `packages/functions/src/repositories/${name}.js`)
  );
  spinner.succeed(`Congratulations! Generate repository: ${name} successfully`);
}
