/**
 * Capitalize a string
 *
 * @param {string} value
 * @return {string}
 */
export function capitalize(value: string) {
  return value.charAt(0).toUpperCase() + value.slice(1);
}

/**
 * Just show a banner to console
 * @see http://patorjk.com/software/taag/#p=display&h=2&v=0&f=Small%20Slant&t=Magee
 */
export function showBanner(version: string = '0.0.14') {
  // tslint:disable-next-line:no-console
  console.log(`
    __               __
  / _ |_  _____ ____/ /__ _
 / __ | |/ / _ \`/ _  / _ \`/
/_/ |_|___/\\_,_/\\_,_/\\_,_/
version: ${version}
  `);
}

/**
 * Convert a string from camelCase to snack_case
 *
 * @param {string} value
 * @return {string}
 * @see https://vladimir-ivanov.net/camelcase-to-snake_case-and-vice-versa-with-javascript/
 */
export function camelToSnake(value: string) {
  return value
    .replace(/[\w]([A-Z])/g, m => {
      return m[0] + '_' + m[1];
    })
    .toLowerCase();
}
